    private void btnTextAreaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnTextAreaActionPerformed
        // TODO add your handling code here:
        if(txtUserInput.getText().trim().isEmpty()){
            JOptionPane.showMessageDialog(frame_ui.this, "Enter Your Name");
            txtUserInput.grabFocus();
            return;
        }
        JOptionPane.showMessageDialog(frame_ui.this, "Name :"+ txtUserInput.getText());
    }//GEN-LAST:event_btnTextAreaActionPerformed

