    private void btnGetCityActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnGetCityActionPerformed
        // TODO add your handling code here:
        if(jComboBox1.getSelectedIndex()==0){
            JOptionPane.showMessageDialog(frame_ui.this, "Select any city first");
            return;
        }

        JOptionPane.showMessageDialog(frame_ui.this, "Selected city :" + jComboBox1.getSelectedItem());
        return;

    }//GEN-LAST:event_btnGetCityActionPerformed

