    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        // TODO add your handling code here:
        if(chkhtml.isSelected()){
            JOptionPane.showMessageDialog(frame_ui.this, chkhtml.getText());
        }
        else if(chkc.isSelected()){
            JOptionPane.showMessageDialog(frame_ui.this, chkc.getText());
        }
        else if(chkphp.isSelected()){
            JOptionPane.showMessageDialog(frame_ui.this, chkphp.getText());
        }
        else if(chkjava.isSelected()){
            JOptionPane.showMessageDialog(frame_ui.this, chkjava.getText());
        }
    }//GEN-LAST:event_jButton3ActionPerformed

